}

function swell(object){
    var direction = object.components.combined.direction;
    var period = object.components.combined.period;
    var min = object.minBreakingHeight
    var max = object.maxBreakingHeight;
    var hight = object.components.combined.height;
    if(Object.keys(object.components).length > 2){
       alterSwell = Object.keys(object.components).length-2; 
    } 
    else{
        alterSwell = Object.keys(object.components).length-2;
        if (!alterSwell) alterSwell = " No other swells";
    }
    var swell={
           Direction: direction,
           Period: period,
           Min: min,
           Max: max,
           Hight: hight,
           AnoterSwell: alterSwell
    }
    return swell;
}

function wind(object){
    var direction = object.wind.direction;
    var speed = object.wind.speed;
    var gusts = object.wind.gusts;
    var temp = object.condition.temperature;

    var wind = {
            Direction: direction,
            Speed: speed,
            Gusts: gusts,
            Temperature: temp
    }
    return wind;
}

